mapper.select:
select b.b_no, b.b_title, b.b_content, b.b_sysdate, b.b_views, a.division as writer
from board b, admin a, member m
where b.b_writer_code=m.id and a.code=m.code and b.b_no=#{b_no}

mapper.update:
update board
set b_title=#{b_title}, b_content=#{b_content}, b_sort=#{b_sort}
where b_no=#{b_no}
